/* eslint-disable no-unused-vars */
/* eslint-disable react/prop-types */
import React, { useState } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import {
  updateLoggedInUserFollowing,
  updateFollowerdUserFollowers,
} from "../../services/firebase";

export default function SuggestedProfile({
  profileDocId,
  username,
  profileId,
  userId,
  loggedInUserDocId,
}) {
  const [followed, setFollowed] = useState(false);

  async function handleFollowedUser() {
    setFollowed(true);

    // firebase: create 2 services (functions)
    // update the following array of the logged in user(in this case, my profile)
    await updateLoggedInUserFollowing(loggedInUserDocId, profileId, false);
    // update the followers array of the user who has been foollwed
    await updateFollowerdUserFollowers(profileDocId, userId, false);
  }
  return !followed ? (
    <div className="flex flex-row items-center align-items justify-between mr-5">
      <div className="flex items-center justify-between">
        <img
          className="rounded-full w-8 flex mr-3"
          src={`/images/avatars/${username}.jpg`}
          alt=""
        />
        <Link to={`/p/${username}`}>
          <p className="font-semibold text-sm">{username}</p>
        </Link>
      </div>
      <button
        className="text-xs font-semibold text-blue-medium"
        type="button"
        onClick={() => handleFollowedUser()}
      >
        Follow
      </button>
    </div>
  ) : null;
}
SuggestedProfile.prototypes = {
  userId: PropTypes.string,
  profileDocId: PropTypes.string,
  profileId: PropTypes.string,
  username: PropTypes.string,
  follwers: PropTypes.string,
  loggedInUserDocId: PropTypes.string.isRequired,
};
